#include "pattern.h"
#include <iostream>

// Open the file to write the pattern generated by me
void PATTERN::setupInput(string pattern_name)
{
	string str = "mkdir ";
	str.append(xstr(INDIR));
	system(str.c_str());                                                               

	str = "./";
	str.append(xstr(INDIR));
	str.append("/");
	str.append(pattern_name);
	ofs.open(str.c_str(), ofstream::out | ofstream::trunc);                            
	if(!ofs.is_open())                                                         
		std::cout << "Cannot open file!\n";
}

void PATTERN::genRandomPattern(string pattern_name, int pattern_number)
{
	setupInput(pattern_name);
	//ofs << pattern_number;
}

void PATTERN::genRandomPatternUnknown(string pattern_name, int pattern_number)
{
	setupInput(pattern_name);
	//ofs << pattern_number;
}
